// –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–≤–∞—Ä—ã —Å –≤—ã—Å–æ–∫–∏–º —Å–ø—Ä–æ—Å–æ–º
const items = $input.all();
const products = items.map(item => item.json);

// –ï—Å–ª–∏ API –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –º–∞—Å—Å–∏–≤ –Ω–∞–ø—Ä—è–º—É—é
const productList = Array.isArray(products[0]) ? products[0] : products;

// –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–≤–∞—Ä—ã —Å favorites_count >= 5000
let highDemandProducts = productList.filter(product => {
  return (product.favorites_count || 0) >= 5000;
});

// –ï—Å–ª–∏ –Ω–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤ —Å favorites_count >= 5000, –±–µ—Ä–µ–º —Ç–æ–ø 50 –ø–æ —Å–ø—Ä–æ—Å—É
if (highDemandProducts.length === 0) {
  console.log('–ù–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤ —Å favorites_count >= 5000, –±–µ—Ä—É —Ç–æ–ø 50 –ø–æ —Å–ø—Ä–æ—Å—É');
  highDemandProducts = productList
    .filter(p => (p.favorites_count || 0) > 0)
    .sort((a, b) => (b.favorites_count || 0) - (a.favorites_count || 0))
    .slice(0, 50);
}

// –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Å–ø—Ä–æ—Å—É
highDemandProducts.sort((a, b) => (b.favorites_count || 0) - (a.favorites_count || 0));

// –ë–µ—Ä–µ–º —Ç–æ–ø 50
const topProducts = highDemandProducts.slice(0, 50);

if (topProducts.length === 0) {
  console.log('‚ö†Ô∏è  –ù–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤ –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏');
  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω —ç–ª–µ–º–µ–Ω—Ç —Å —Å–æ–æ–±—â–µ–Ω–∏–µ–º –æ–± –æ—à–∏–±–∫–µ
  return [{
    json: {
      error: '–ù–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤ —Å –¥–æ—Å—Ç–∞—Ç–æ—á–Ω—ã–º —Å–ø—Ä–æ—Å–æ–º',
      product_id: 'N/A',
      product_name: '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö',
      favorites_count: 0,
      category: 'N/A',
      brand: 'N/A'
    }
  }];
}

return topProducts.map(product => ({
  json: {
    product_id: product.product_id || product.id,
    product_name: product.product_name || product.name,
    favorites_count: product.favorites_count || 0,
    category: product.category_level_1 || product.category,
    brand: product.brand
  }
}));

// –ò–∑–≤–ª–µ–∫–∞–µ–º –¥–∞–Ω–Ω—ã–µ –æ —Å—Ä–∞–≤–Ω–µ–Ω–∏–∏ —Ü–µ–Ω
const response = $input.item.json;
const comparisons = response.comparisons || [];

// –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–≤–∞—Ä—ã, –≥–¥–µ –Ω–∞—à–∞ —Ü–µ–Ω–∞ –∑–Ω–∞—á–∏—Ç–µ–ª—å–Ω–æ –æ—Ç–ª–∏—á–∞–µ—Ç—Å—è –æ—Ç –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤
const significantDiff = comparisons.filter(comp => {
  const diff = Math.abs(comp.price_difference_percent || 0);
  return diff > 10; // –†–∞–∑–Ω–∏—Ü–∞ –±–æ–ª–µ–µ 10%
});

// –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–Ω–æ—Å—Ç–∏ (–≤—ã—Å–æ–∫–∏–π —Å–ø—Ä–æ—Å + –±–æ–ª—å—à–∞—è —Ä–∞–∑–Ω–∏—Ü–∞)
const demandScore = {'high': 3, 'medium': 2, 'low': 1};
significantDiff.sort((a, b) => {
  const scoreA = demandScore[a.demand_level] * 10 + Math.abs(a.price_difference_percent || 0);
  const scoreB = demandScore[b.demand_level] * 10 + Math.abs(b.price_difference_percent || 0);
  return scoreB - scoreA;
});

return significantDiff.map(comp => ({
  json: comp
}));

// –ê–Ω–∞–ª–∏–∑–∏—Ä—É–µ–º —Ü–µ–Ω—ã –∏ –≥–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏
const items = $input.all();
const comparisons = items.map(item => item.json);

// –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
const byCategory = {};
comparisons.forEach(comp => {
  const category = comp.category_level_1 || '–ë–µ–∑ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏';
  if (!byCategory[category]) {
    byCategory[category] = [];
  }
  byCategory[category].push(comp);
});

// –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
const totalProducts = comparisons.length;
const avgPriceDiff = comparisons.reduce((sum, c) => sum + (c.price_difference_percent || 0), 0) / totalProducts;
const tooExpensive = comparisons.filter(c => (c.price_difference_percent || 0) > 10).length;
const tooCheap = comparisons.filter(c => (c.price_difference_percent || 0) < -10).length;

return [{
  json: {
    comparisons: comparisons,
    total_products: totalProducts,
    avg_price_difference: avgPriceDiff.toFixed(2),
    too_expensive_count: tooExpensive,
    too_cheap_count: tooCheap,
    by_category: byCategory,
    timestamp: new Date().toISOString()
  }
}];

// –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º –æ–ø—Ç–∏–º–∞–ª—å–Ω—ã–µ —Ü–µ–Ω—ã
const data = $input.item.json;
const comparisons = data.comparisons || [];

const recommendations = comparisons.map(comp => {
  const ourPrice = comp.our_price || 0;
  const avgCompetitor = comp.avg_competitor_price || 0;
  const minCompetitor = comp.min_competitor_price || 0;
  const maxCompetitor = comp.max_competitor_price || 0;
  const priceDiff = comp.price_difference_percent || 0;

  // –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º –æ–ø—Ç–∏–º–∞–ª—å–Ω—É—é —Ü–µ–Ω—É
  let optimalPrice = avgCompetitor;

  // –ï—Å–ª–∏ –≤—ã—Å–æ–∫–∏–π —Å–ø—Ä–æ—Å, –º–æ–∂–Ω–æ –±—ã—Ç—å –Ω–µ–º–Ω–æ–≥–æ –≤—ã—à–µ —Å—Ä–µ–¥–Ω–µ–π —Ü–µ–Ω—ã
  if (comp.demand_level === 'high') {
    optimalPrice = avgCompetitor * 1.05; // +5% –ø—Ä–∏ –≤—ã—Å–æ–∫–æ–º —Å–ø—Ä–æ—Å–µ
  } else if (comp.demand_level === 'medium') {
    optimalPrice = avgCompetitor * 1.0; // –°—Ä–µ–¥–Ω—è—è —Ü–µ–Ω–∞
  } else {
    optimalPrice = avgCompetitor * 0.95; // -5% –ø—Ä–∏ –Ω–∏–∑–∫–æ–º —Å–ø—Ä–æ—Å–µ
  }

  // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –¥–∏–∞–ø–∞–∑–æ–Ω–æ–º –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤
  optimalPrice = Math.max(minCompetitor * 0.9, Math.min(maxCompetitor * 1.1, optimalPrice));

  const priceChange = optimalPrice - ourPrice;
  const priceChangePercent = ((priceChange / ourPrice) * 100).toFixed(2);

  return {
    json: {
      product_id: comp.product_id,
      product_name: comp.product_name,
      brand: comp.brand,
      category: comp.category_level_1,
      current_price: ourPrice,
      optimal_price: Math.round(optimalPrice),
      price_change: Math.round(priceChange),
      price_change_percent: priceChangePercent,
      avg_competitor_price: avgCompetitor,
      demand_level: comp.demand_level,
      recommendation: comp.recommendation
    }
  };
});

return recommendations;

// –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –æ—Ç—á–µ—Ç–∞
const items = $input.all();
const recommendations = items.map(item => item.json);

let message = `üí∞ <b>–ê–ù–ê–õ–ò–ó –¶–ï–ù –ö–û–ù–ö–£–†–ï–ù–¢–û–í</b>\n\n`;
message += `üìÖ –î–∞—Ç–∞: ${new Date().toLocaleString('ru-RU')}\n`;
message += `üìä –í—Å–µ–≥–æ —Ç–æ–≤–∞—Ä–æ–≤ –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–æ: ${recommendations.length}\n\n`;
message += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;

// –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–ø 10 —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π
const topRecommendations = recommendations.slice(0, 10);
topRecommendations.forEach((rec, index) => {
  const changeIcon = parseFloat(rec.price_change_percent) > 0 ? 'üìà' : 'üìâ';
  const demandIcon = rec.demand_level === 'high' ? 'üî•' : rec.demand_level === 'medium' ? '‚ö°' : 'üìä';

  message += `${index + 1}. ${demandIcon} <b>${rec.product_name}</b>\n`;
  message += `   üè∑Ô∏è –ë—Ä–µ–Ω–¥: ${rec.brand || '–ë–µ–∑ –±—Ä–µ–Ω–¥–∞'}\n`;
  message += `   üì¶ –ö–∞—Ç–µ–≥–æ—Ä–∏—è: ${rec.category || '–ù–µ —É–∫–∞–∑–∞–Ω–∞'}\n`;
  message += `   üíµ –¢–µ–∫—É—â–∞—è —Ü–µ–Ω–∞: <s>${rec.current_price}‚ÇΩ</s>\n`;
  message += `   ${changeIcon} –û–ø—Ç–∏–º–∞–ª—å–Ω–∞—è —Ü–µ–Ω–∞: <b>${rec.optimal_price}‚ÇΩ</b>\n`;
  message += `   üìä –ò–∑–º–µ–Ω–µ–Ω–∏–µ: ${rec.price_change > 0 ? '+' : ''}${rec.price_change}‚ÇΩ (${rec.price_change_percent > 0 ? '+' : ''}${rec.price_change_percent}%)\n`;
  message += `   üìà –°—Ä–µ–¥–Ω—è—è —Ü–µ–Ω–∞ –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤: ${rec.avg_competitor_price.toFixed(2)}‚ÇΩ\n`;
  message += `   üí° ${rec.recommendation}\n\n`;
});

if (recommendations.length > 10) {
  message += `\n... –∏ –µ—â–µ ${recommendations.length - 10} —Ç–æ–≤–∞—Ä–æ–≤\n`;
}

message += `\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
message += `üí° <i>–†–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è –æ–±–Ω–æ–≤–∏—Ç—å —Ü–µ–Ω—ã –¥–ª—è –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ—Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç–∏</i>`;

return [{
  json: {
    formatted_message: message,
    html_message: message.replace(/\n/g, '<br>'),
    total_products: recommendations.length,
    recommendations: recommendations,
    summary: {
      avg_price_change: (recommendations.reduce((sum, r) => sum + parseFloat(r.price_change_percent || 0), 0) / recommendations.length).toFixed(2)
    }
  }
}];

// –ö—Ä–∞—Å–∏–≤—ã–π –≤—ã–≤–æ–¥ –¥–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –≤ n8n
const data = $input.item.json;
const recommendations = data.recommendations || [];

let output = `üí∞ –ê–ù–ê–õ–ò–ó –¶–ï–ù –ö–û–ù–ö–£–†–ï–ù–¢–û–í\n\n`;
output += `üìä –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞:\n`;
output += `   ‚Ä¢ –í—Å–µ–≥–æ —Ç–æ–≤–∞—Ä–æ–≤: ${data.total_products}\n`;
output += `   ‚Ä¢ –°—Ä–µ–¥–Ω–µ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Ü–µ–Ω—ã: ${data.summary.avg_price_change}%\n\n`;
output += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;

recommendations.slice(0, 20).forEach((rec, index) => {
  output += `${index + 1}. ${rec.product_name}\n`;
  output += `   –¢–µ–∫—É—â–∞—è: ${rec.current_price}‚ÇΩ ‚Üí –û–ø—Ç–∏–º–∞–ª—å–Ω–∞—è: ${rec.optimal_price}‚ÇΩ (${rec.price_change_percent > 0 ? '+' : ''}${rec.price_change_percent}%)\n\n`;
});

return [{
  json: {
    "üí∞ –ê–Ω–∞–ª–∏–∑ —Ü–µ–Ω –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤": output,
    "–í—Å–µ–≥–æ —Ç–æ–≤–∞—Ä–æ–≤": data.total_products,
    "–°—Ä–µ–¥–Ω–µ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ": `${data.summary.avg_price_change}%`,
    "–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏": recommendations.map(r => ({
      "–¢–æ–≤–∞—Ä": r.product_name,
      "–¢–µ–∫—É—â–∞—è —Ü–µ–Ω–∞": `${r.current_price}‚ÇΩ`,
      "–û–ø—Ç–∏–º–∞–ª—å–Ω–∞—è —Ü–µ–Ω–∞": `${r.optimal_price}‚ÇΩ`,
      "–ò–∑–º–µ–Ω–µ–Ω–∏–µ": `${r.price_change_percent > 0 ? '+' : ''}${r.price_change_percent}%`,
      "–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è": r.recommendation
    }))
  }
}];
